contracts/ResiRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "./interfaces/IResiRegistry.sol";
import "./interfaces/IResiVault.sol";

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/// @title Resi Registry Contract
/// @author Alejo Lovallo
/// @notice Registry holding information of series and projects
contract ResiRegistry is IResiRegistry, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    /// @dev Resi token address
    address public RESI_TOKEN;

    /// @dev Global Treasury vault address handled by admin protocol
    address private TREASURY_VAULT;

    /// @dev current serie running
    uint256 private activeSerieId;

    /// @dev serieId => Serie info
    mapping(uint256 => Serie) public series;
    /// @dev serieId => SBT
    mapping(uint256 => address) public seriesSBTs;
    /// @dev project name => Project info
    mapping(bytes32 => Project) public projects;

    using SafeERC20 for IERC20;

    function initialize() public initializer {
        __Context_init_unchained();
        __Ownable_init_unchained();
        __ReentrancyGuard_init_unchained();
        emit RegistryInitialized();
    }

    /**************************** GETTERS  ****************************/

    /**
     * @dev Get current active serie
     * @return id
     */
    function activeSerie() external view returns (uint256 id) {
        id = activeSerieId;
    }

    /**
     * @dev Know if a project is valid from a specific serie
     * @param _serie serie id
     * @param _project project name
     * @return if valid
     */
    function isValidProject(uint256 _serie, bytes32 _project) external view returns (bool) {
        if (_serie == activeSerieId && projects[_project].serie == activeSerieId && projects[_project].active) {
            return true;
        }
        return false;
    }

    /**
     * @dev Know if a project in an active serie is valid
     * @param _project project name
     * @return if valid
     */
    function isValidProject(bytes32 _project) external view returns (bool) {
        Project memory proj = projects[_project];
        if (proj.serie == activeSerieId && proj.active == true) {
            return true;
        }
        return false;
    }

    /**
     * @dev Get active serie sbt token
     * @return sbt address
     */
    function getSBTSerie() external view returns (address) {
        return seriesSBTs[activeSerieId];
    }

    /**
     * @dev Get serie SBT token for a specific serie
     * @param _serieId serie id to get sbt
     * @return sbt address
     */
    function getSBTSerie(uint256 _serieId) external view returns (address) {
        return seriesSBTs[_serieId];
    }

    /**
     * @dev Get serie active state and current supply emitted
     * @param _serieId serie id to get state
     * @return wether serie is active
     * @return currenty serie supply emitted
     */
    function getSerieState(uint256 _serieId) external view returns (bool, uint256) {
        bool isActive = series[_serieId].active;
        uint256 currentSupply = series[_serieId].currentSupply;
        return (isActive, currentSupply);
    }

    /**
     * @dev Get current serie supply minted
     * @param _serieId serie id to get current supply
     * @return supply minted
     */
    function getSerieSupply(uint256 _serieId) external view returns (uint256) {
        return series[_serieId].currentSupply;
    }

    /**
     * @dev Get Treasury vault address
     * @return treasury addresss
     */
    function getTreasuryVault() external view returns (address) {
        return TREASURY_VAULT;
    }

    /**************************** INTERFACE  ****************************/

    /**
     * @dev Set Resi token for Registry
     * @param _resiToken address of the token
     */
    function setResiToken(address _resiToken) external onlyOwner {
        require(_resiToken != address(0), "ResiRegistry: INVALID TOKEN ADDRESS");
        RESI_TOKEN = _resiToken;
        emit ResiTokenSet(_resiToken);
    }

    /**
     * @dev Set Treasury Vault for Registry
     * @param _treasuryVault treasury vault address
     */
    function setTreasuryVault(address _treasuryVault) external onlyOwner {
        require(_treasuryVault != address(0), "ResiRegistry: INVALID VAULT ADDRESS");
        TREASURY_VAULT = _treasuryVault;
        emit TreasuryVaultSet(_treasuryVault);
    }

    /**
     * @dev Create New Serie. Cannot be created if another serie is running
     * @param _startDate timestamp when serie starts
     * @param _endDate timestamp when serie ends
     * @param _numberOfProjects amount of projects
     * @param _maxSupply max supply to mint during serie
     * @param _vault address of the serie vault
     */
    function createSerie(
        uint256 _startDate,
        uint256 _endDate,
        uint256 _numberOfProjects,
        uint256 _maxSupply,
        address _vault
    ) external onlyOwner {
        _checkSerie(_startDate, _endDate, _numberOfProjects, _maxSupply, _vault);
        activeSerieId += 1;
        Serie memory newSerie = Serie({
            id: activeSerieId,
            startDate: _startDate,
            endDate: _endDate,
            numberOfProjects: _numberOfProjects,
            currentProjects: 0,
            currentSupply: 0,
            maxSupply: _maxSupply,
            vault: _vault,
            active: true,
            created: true
        });
        series[activeSerieId] = newSerie;
        emit SerieCreated(activeSerieId, _startDate, _endDate, _numberOfProjects, _maxSupply, _vault);
    }

    /**
     * @dev Add project to current serie
     * @param _name name of the project
     */
    function addProject(bytes32 _name) external onlyOwner {
        _addProject(_name);
    }

    /**
     * @dev Add projects to current serie
     * @param names projects names
     */
    function addProjects(bytes32[] memory names) external onlyOwner {
        for (uint256 i = 0; i < names.length; i++) {
            _addProject(names[i]);
        }
    }

    /**
     * @dev Disable project (no matter if serie not active)
     * @param _name project name
     */
    function disableProject(bytes32 _name) external onlyOwner {
        projects[_name].active = false;
        emit ProjectDisabled(_name);
    }

    /**
     * @dev Set SBT for current serie
     * @param _sbt address of the SBT token
     */
    function registerSerieSBT(address _sbt) external onlyOwner {
        require(series[activeSerieId].active, "ResiRegistry: SERIE NOT ACTIVE");
        require(_sbt != address(0), "ResiRegistry: INVALID SBT ADDRESS");
        seriesSBTs[activeSerieId] = _sbt;
        emit SerieSBTSet(activeSerieId, _sbt);
    }

    /**
     * @dev Increase serie supply. Only performed by ResiToken
     * @param _serieId serie id
     * @param _amount amount to increase
     */
    function increaseSerieSupply(uint256 _serieId, uint256 _amount) external onlyRESIToken nonReentrant {
        _increaseSerieSupply(_serieId, _amount);
    }

    /**
     *
     * @dev Decrease serie supply. Only performed by ResiToken
     * @param _serieId serie id
     * @param _amount amount to decrease
     */
    function decreaseSerieSupply(uint256 _serieId, uint256 _amount) external onlyRESIToken nonReentrant {
        _decreaseSerieSupply(_serieId, _amount);
    }

    /**
     * @dev Close serie. This will mean all kind of operations to a serie could not be done anymore.
     */
    function closeSerie() external onlyOwner {
        require(series[activeSerieId].created, "ResiRegistry: SERIE NOT CREATED YET");
        require(block.timestamp >= series[activeSerieId].endDate, "ResiRegistry: SERIE STILL ACTIVE");
        series[activeSerieId].active = false;
        emit SerieClosed(activeSerieId);
    }

    /**
     * @dev
     * @param _serieId serie id
     * @param _amount amount to withdrawn from vault
     * @param _to address who will receive the assets
     */
    function withdrawFromVault(uint256 _serieId, uint256 _amount, address _to) external onlyRESIToken {
        require(!series[_serieId].active, "ResiRegistry: SERIE STILL ACTIVE");
        require(_amount > 0, "ResiRegistry: INVALID AMOUNT");
        require(_to != address(0), "ResiRegistry: INVALID RECEIVER");
        require(series[_serieId].currentSupply > 0, "ResiRegistry: NO MORE SUPPLY TO WITHDRAW");
        require(series[_serieId].currentSupply - _amount > 0, "ResiRegistry: INVALID WITHDRAW AMOUNT");

        address vaultToken = IResiVault(series[_serieId].vault).getMainToken();

        uint256 beforeBalance = IERC20(vaultToken).balanceOf(address(this));
        IResiVault(series[_serieId].vault).release(_amount);
        uint256 afterBalance = IERC20(vaultToken).balanceOf(address(this));

        require(afterBalance > beforeBalance, "ResiRegistry: SOMETHING WENT WRONG WITHDRAWING FROM VAULT");

        IERC20(vaultToken).safeTransfer(_to, afterBalance);

        emit WithdrawFromVault(_serieId, _amount, _to);
    }

    /**************************** INTERNALS  ****************************/

    /**
     * @dev performs security check before adding a new serie
     * @param _startDate timestamp serie will start
     * @param _endDate tiemstamp serie will end
     * @param _numberOfProjects number of projects allowed to be in the serie
     * @param _maxSupply max supply to emit through serie
     * @param _vault vault address
     */
    function _checkSerie(
        uint256 _startDate,
        uint256 _endDate,
        uint256 _numberOfProjects,
        uint256 _maxSupply,
        address _vault
    ) internal view onlyOwner {
        require(!(series[activeSerieId].active), "ResiRegistry: CURRENT SERIE IS NOT CLOSED YET");
        require(_startDate >= block.timestamp, "ResiRegistry: INVALID START DATE");
        require(_endDate >= _startDate, "ResiRegistry: INVALID END DATE");
        require(_numberOfProjects > 0, "ResiRegistry: PROJECTS MUST BE MORE THAN ZERO");
        require(_maxSupply > 0, "ResiRegistry: MAX SUPPLY TO EMIT MSUT BE GREATER THAN ZERO");
        require(_vault != address(0), "ResiRegistry: INVALID VAULT CONTRACT");
        // TODO: review here require(isContract(_vault), "VAULT MUST BE CONTRACT");
    }

    /**
     * @dev internal function for addProject
     * @param _name project name
     */
    function _addProject(bytes32 _name) internal onlyOwner {
        require(series[activeSerieId].created, "ResiRegistry: SERIE INACTIVE");
        require(_name != bytes32(0), "ResiRegistry: INVALID NAME");
        require(
            series[activeSerieId].currentProjects < series[activeSerieId].numberOfProjects,
            "ResiRegistry: MAX PROJECTS SERIES REACHED"
        );
        series[activeSerieId].currentProjects++;
        Project memory newProject = Project({serie: activeSerieId, active: true});
        projects[_name] = newProject;
        emit ProjectAdded(_name, activeSerieId);
    }

    /**
     * @dev private function for increaseSerieSupply
     * @param _serieId serie id
     * @param _amount amount to increase
     */
    function _increaseSerieSupply(uint256 _serieId, uint256 _amount) private {
        require(series[_serieId].created, "ResiRegistry: INVALID SERIE");
        require(series[_serieId].active, "ResiRegistry: SERIE INACTIVE");
        require(_amount > 0, "ResiRegistry: INVALID AMOUNT");
        require(
            series[_serieId].currentSupply + _amount <= series[_serieId].maxSupply,
            "ResiRegistry: Amount will exceed serie max supply"
        );
        uint256 oldSupply = series[_serieId].currentSupply;
        series[_serieId].currentSupply += _amount;
        emit SerieSupplyUpdated(oldSupply, series[_serieId].currentSupply);
    }

    /**
     * @dev private function for decreaseSerieSupply
     * @param _serieId serie id
     * @param _amount amount to decrease
     */
    function _decreaseSerieSupply(uint256 _serieId, uint256 _amount) private {
        require(series[_serieId].created, "ResiRegistry: INVALID SERIE");
        require(_amount > 0, "ResiRegistry: INVALID AMOUNT");
        uint256 oldSupply = series[_serieId].currentSupply;
        series[_serieId].currentSupply -= _amount;
        emit SerieSupplyUpdated(oldSupply, series[_serieId].currentSupply);
    }

    /**
     * @dev Modifier to perform actions only by ResiToken contract
     */
    modifier onlyRESIToken() {
        require(_msgSender() == RESI_TOKEN, "ResiRegistry: ONLY RESI TOKEN");
        _;
    }

    /// @dev Leave a gap betweeen inherited contracts variables in order
    /// @dev to be able to add more variables in them later.
    uint256[20] private upgradeGap;
}

contracts/ResiSBT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/IAccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "./interfaces/IResiSBT.sol";
import "./interfaces/IResiRegistry.sol";
import "./interfaces/IResiToken.sol";
import "./interfaces/IERC5192.sol";

/// @title Resi SBT Contract
/// @author Alejo Lovallo
/// @notice SBT token linked to a Serie
contract ResiSBT is IResiSBT, IERC5192, OwnableUpgradeable, ERC721URIStorageUpgradeable, ReentrancyGuardUpgradeable {
    using Counters for Counters.Counter;
    /// @dev Private counter to make internal security checks
    Counters.Counter private _tokenIdCounter;

    /// @dev Token ContractUri
    string public contractUri;
    /// @dev Serie id
    uint256 public SERIE_ID;
    /// @dev Resi ERC20 token contract
    address public RESI_TOKEN;
    /// @dev Resi Registry contract
    address public RESI_REGISTRY;

    /// @dev user => nickname
    mapping(address => bytes32) public userNickNames;
    /// @dev user => resi erc20 balances
    mapping(address => uint256) public resiTokenBalances;
    /// @dev tokenId => isLocked
    mapping(uint256 => bool) private lockedSBTs;
    /// @dev role => uri
    mapping(bytes32 => string) private defaultRoleUris;

    function initialize(
        string memory _name,
        string memory _symbol,
        string memory _contractUri,
        uint256 _serieId,
        address _registry,
        address _token
    ) external initializer {
        require(_registry != address(0), "INVALID REGISTRY ADDRESS");
        require(_token != address(0), "INVALID RESI TOKEN ADDRESS");
        __Context_init_unchained();
        __Ownable_init_unchained();
        __ReentrancyGuard_init_unchained();
        __ERC721_init_unchained(_name, _symbol);
        __ERC721URIStorage_init_unchained();

        RESI_REGISTRY = _registry;
        RESI_TOKEN = _token;
        SERIE_ID = _serieId;
        contractUri = _contractUri;

        emit ResiSBTInitialized(_name, _symbol, _serieId, _registry, _token);
    }

    /**************************** GETTERS  ****************************/
    /**
     * @dev Retrieve token uri from id
     * @param tokenId token id to retrieve uri
     * @return uri
     * @custom:experimental The following function is override required by Solidity.
     */
    function tokenURI(uint256 tokenId) public view override(ERC721URIStorageUpgradeable) returns (string memory) {
        return super.tokenURI(tokenId);
    }

    /**
     * @dev Know if a sbt is locked
     * @param tokenId sbt token id
     * @return is sbt locked
     */
    function locked(uint256 tokenId) external view returns (bool) {
        return lockedSBTs[tokenId];
    }

    /**
     * @dev check if user is able to receive SBT
     * @param _account receiver address
     * @param _role acount role granted by ResiToken
     * @param _serieId serie where sbt belongs to
     * @return whether is sbt receiver
     */
    function isSBTReceiver(address _account, bytes32 _role, uint256 _serieId) public view returns (bool) {
        if (
            IAccessControlUpgradeable(RESI_TOKEN).hasRole(_role, _account) &&
            IResiRegistry(RESI_REGISTRY).activeSerie() == _serieId
        ) {
            return true;
        }
        return false;
    }

    /**************************** INTERFACE  ****************************/

    /**
     * @dev Set SBT owner nickname/username
     * @param _nickname sbt username
     */
    function setNickName(bytes32 _nickname) external {
        require(balanceOf(_msgSender()) == 1, "ResiSBT: NOT AN SBT OWNER");
        require(_nickname != bytes32(0), "ResiSBT: INVALID NICKNAME");
        userNickNames[_msgSender()] = _nickname;
        emit NicknameUpdated(_msgSender(), _nickname);
    }

    /// @notice Modify contractUri for NFT collection
    /// @param _contractUri contractUri
    function setContractURI(string memory _contractUri) external onlyOwner {
        contractUri = _contractUri;
        emit ContractURIUpdated(contractUri);
    }

    /**
     * @dev Set Resi Registry
     * @param _registry registry address
     */
    function setRegistry(address _registry) external onlyOwner {
        require(_registry != address(0), "INVALID REGISTRY ADDRESS");
        RESI_REGISTRY = _registry;
        emit RegistrySet(_registry);
    }

    /**
     * @dev Set default Resi Token Role URI
     * @param _role role name
     * @param _uri default uri
     */
    function setDefaultRoleUri(bytes32 _role, string calldata _uri) external onlyOwner {
        require(_role != bytes32(0), "ResiSBT: INVALID ROLE");
        require(bytes(_uri).length > 0, "ResiSBT: Empty URI");
        string memory oldUri = defaultRoleUris[_role];
        defaultRoleUris[_role] = _uri;
        emit DefaultRoleUriUpdated(oldUri, _uri);
    }

    /**
     * @dev Mint SBT to user with specific role and uri.
     * @param _to user
     * @param _role role name from Resi registry
     * @param _uri uri
     */
    function mint(address _to, bytes32 _role, string memory _uri) external onlyOwner returns (uint256) {
        uint256 tokenId = _mintSBT(_to, _role, _uri);
        emit MintSBT(_to, _role, tokenId);
        return tokenId;
    }

    /**
     * @dev Mint to more than one user sbts by specific role
     * @param _to array of users
     * @param _uri token uri for the sbts
     * @param _role role that all users must have
     */
    function mintBatchByRole(address[] memory _to, string memory _uri, bytes32 _role) external onlyOwner {
        for (uint256 i = 0; i < _to.length; i++) {
            _checkMint(_to[i], _role, _uri);
            _mintSBT(_to[i], _role, _uri);
        }
    }

    /**
     * @dev Mint function performed only by ResiToken contract
     * @param _to user to mint sbt
     * @param _role user role
     */
    function mintByResiToken(address _to, bytes32 _role) external onlyResiToken nonReentrant {
        _mintByResiToken(_to, _role);
    }

    /**
     *  @dev Increase resi token balance
     * @param _to user address
     * @param _amount amount to increase
     */
    function increaseResiTokenBalance(address _to, uint256 _amount) external onlyResiToken nonReentrant {
        _increaseResiTokenBalance(_to, _amount);
    }

    /**
     * @dev Decrease resi token balance
     * @param _to user address
     * @param _amount amoutn to decrease
     */
    function decreaseResiTokenBalance(address _to, uint256 _amount) external onlyResiToken nonReentrant {
        _decreaseResiTokenBalance(_to, _amount);
    }

    /**************************** INTERNALS  ****************************/

    /**
     * @dev Internal function to mint an sbt to a user
     * @param _to user address
     * @param _role user role
     * @param _uri sbt uri
     * @return sbt id
     */
    function _mintSBT(address _to, bytes32 _role, string memory _uri) internal returns (uint256) {
        require(_msgSender() == owner() || _msgSender() == RESI_TOKEN, "ResiSBT: ONLY OWNER OR RESI TOKEN");
        _checkMint(_to, _role, _uri);
        uint256 _tokenId = _tokenIdCounter.current();

        //mint sbt
        lockedSBTs[_tokenId] = true;
        _safeMint(_to, _tokenId);
        super._setTokenURI(_tokenId, _uri);

        _tokenIdCounter.increment();

        emit Locked(_tokenId);
        return _tokenId;
    }

    /**
     *  @dev Internal function to mint sbt by ResiToken contract
     * @param _to user address
     * @param _role user role
     */
    function _mintByResiToken(address _to, bytes32 _role) private {
        string memory defaultUri = defaultRoleUris[_role];
        require(bytes(defaultUri).length > 0, "ResiSBT: Default Role Uri not set");
        uint256 tokenId = _mintSBT(_to, _role, defaultUri);
        emit SBTMintedByResiToken(_to, _role, tokenId);
    }

    /**
     * @dev internal function to increase resitoken balance
     * @param _to user address
     * @param _amount amouunt to increase
     */
    function _increaseResiTokenBalance(address _to, uint256 _amount) private {
        require(balanceOf(_to) == 1, "ResiSBT: User has no SBT");
        require(_amount > 0, "ResiSBT: Invalid amount");
        resiTokenBalances[_to] += _amount;
        emit IncreaseResiBalance(_to, _amount);
    }

    /**
     *  @dev internal function to decrease resitoken balance
     * @param _to user address
     * @param _amount amount to decrease
     */
    function _decreaseResiTokenBalance(address _to, uint256 _amount) private {
        require(balanceOf(_to) == 1, "ResiSBT: User has no SBT");
        require(_amount > 0, "ResiSBT: Invalid amount");
        resiTokenBalances[_to] -= _amount;
        emit DecreaseResiBalance(_to, _amount);
    }

    /**
     *  @dev internal function to check mint conditions
     * @param _to user address
     * @param _role user role
     * @param uri sbt uri
     */
    function _checkMint(address _to, bytes32 _role, string memory uri) internal view {
        require(_msgSender() == owner() || _msgSender() == RESI_TOKEN, "ResiSBT: ONLY OWNER OR RESI TOKEN");
        require(_to != address(0), "ResiSBT: INVALID TO ADDRESS");
        require(balanceOf(_to) == 0, "ResiSBT: USER ALREADY HAS SBT");
        require(bytes(uri).length > 0, "ResiSBT: EMPTY URI");
        require(isSBTReceiver(_to, _role, SERIE_ID), "ResiSBT: INVALID SBT RECEIVER");
    }

    /**
     * @dev See {ERC721-_beforeTokenTransfer}.
     */
    function _beforeTokenTransfer(address from, address to, uint256 firstTokenId, uint256 batchSize) internal override {
        super._beforeTokenTransfer(from, to, firstTokenId, batchSize);
    }

    /**
     *  @dev Transfer from is forbidden in SBT contract
     */
    function transferFrom(address, address, uint256) public pure override {
        revert TransferForbidden("ResiSBT: NO TRANSFER FROM ALLOWED");
    }

    /**
     * @dev Safe transfer from is forbidden in SBT contract
     */
    function safeTransferFrom(address, address, uint256) public pure override {
        revert TransferForbidden("ResiSBT: NO TRANSFER FROM ALLOWED");
    }

    /**
     * @dev Safe transfer from is forbidden in SBT contract
     */
    function safeTransferFrom(address, address, uint256, bytes memory) public pure override {
        revert TransferForbidden("ResiSBT: NO TRANSFER FROM ALLOWED");
    }

    /**
     * @dev Modifier to perform actions only by ResiToken contract
     */
    modifier onlyResiToken() {
        require(_msgSender() == RESI_TOKEN, "ResiSBT: INVALID RESI TOKEN ADDRESS");
        _;
    }

    /// @dev Leave a gap betweeen inherited contracts variables in order
    /// @dev to be able to add more variables in them later.
    uint256[20] private upgradeGap;
}

contracts/ResiToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "./interfaces/IResiToken.sol";
import "./interfaces/IResiRegistry.sol";
import "./interfaces/IResiSBT.sol";

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @title Resi Token Contract
/// @author Alejo Lovallo
/// @notice ERC20 Resi Token
contract ResiToken is
    IResiToken,
    AccessControlEnumerableUpgradeable,
    ERC20Upgradeable,
    ERC20BurnableUpgradeable,
    ERC20PausableUpgradeable,
    ReentrancyGuardUpgradeable
{
    ///@dev ADMIN ROLE
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    ///@dev MENTOR ROLE
    bytes32 public constant MENTOR_ROLE = keccak256("MENTOR_ROLE");
    ///@dev TREASURY ROLE
    bytes32 public constant TREASURY_ROLE = keccak256("TREASURY_ROLE");
    ///@dev PROJECT BUILDER ROLE
    bytes32 public constant PROJECT_BUILDER_ROLE = keccak256("PROJECT_BUILDER_ROLE");
    ///@dev RESI BUILDER ROLE
    bytes32 public constant RESI_BUILDER_ROLE = keccak256("RESI_BUILDER_ROLE");

    /// @dev Resi Registry contract
    address public RESI_REGISTRY;

    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.Bytes32Set;
    EnumerableSetUpgradeable.Bytes32Set private _rolesSet;

    function initialize(address _treasury, address _registry) public initializer {
        require(_treasury != address(0), "INVALID TREASURY ADDRESS");
        require(_registry != address(0), "INVALID REGISTRY ADDRESS");
        __Context_init_unchained();
        __AccessControl_init_unchained();
        __ReentrancyGuard_init_unchained();
        __ERC20_init_unchained("ResiToken", "RESI");
        __ERC20Burnable_init_unchained();
        __ERC20Pausable_init_unchained();

        ///@dev Add roles to the set of Roles for later tracking
        _rolesSet.add(MENTOR_ROLE);
        _rolesSet.add(PROJECT_BUILDER_ROLE);
        _rolesSet.add(RESI_BUILDER_ROLE);

        _grantRole(ADMIN_ROLE, _msgSender());
        _grantRole(TREASURY_ROLE, _treasury);

        ///@dev Admin role can add/remove admins in addition to add/remove all other roles
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
        _setRoleAdmin(TREASURY_ROLE, ADMIN_ROLE);
        _setRoleAdmin(PROJECT_BUILDER_ROLE, ADMIN_ROLE);
        _setRoleAdmin(RESI_BUILDER_ROLE, ADMIN_ROLE);

        RESI_REGISTRY = _registry;

        emit TokenInitialized(_treasury, _registry);
    }

    /**************************** GETTERS  ****************************/

    /**
     * @dev Get amount of roles
     * @return amount of roles
     */
    function getRoleCount() external view returns (uint256) {
        return _rolesSet.length();
    }

    /**************************** INTERFACE  ****************************/

    /**
     * @dev assign MENTOR ROLE to user for specific project
     * @param _mentor user address
     * @param _serieId serie id
     * @param _project project name
     */
    function addMentor(
        address _mentor,
        uint256 _serieId,
        bytes32 _project
    ) external isValidAddress(_mentor, "ResiToken: INVALID MENTOR ADDRESS") onlyRole(ADMIN_ROLE) whenNotPaused {
        _checkSerieAndProject(_serieId, _project);
        _grantRole(MENTOR_ROLE, _mentor);
        emit MentorAdded(_mentor, _project);
    }

    /**
     * @dev assign PROJECT BUILDER role to a user for specific project
     * @param _builder user address
     * @param _serieId serie id
     * @param _project project name
     */
    function addProjectBuilder(
        address _builder,
        uint256 _serieId,
        bytes32 _project
    ) external isValidAddress(_builder, "ResiToken: INVALID BUILDER ADDRESS") onlyRole(ADMIN_ROLE) whenNotPaused {
        _checkSerieAndProject(_serieId, _project);
        _grantRole(PROJECT_BUILDER_ROLE, _builder);
        emit ProjectBuilderAdded(_builder);
    }

    /**
     * @dev assign RESI BUILDER role to user
     * @param _builder user address
     */
    function addResiBuilder(
        address _builder
    ) external isValidAddress(_builder, "ResiToken: INVALID BUILDER ADDRESS") onlyRole(ADMIN_ROLE) whenNotPaused {
        _grantRole(RESI_BUILDER_ROLE, _builder);
        emit ResiBuilderAdded(_builder);
    }

    /**
     *  @dev assign specific role to more than one user
     * @param _role role to assign
     * @param _addresses users array
     */
    function addRolesBatch(bytes32 _role, address[] memory _addresses) external onlyRole(ADMIN_ROLE) whenNotPaused {
        _addRolesBatch(_role, _addresses);
    }

    /**
     *  @dev unassign role for user
     * @param _role role
     * @param _user user address to remove
     */
    function removeUserRole(
        bytes32 _role,
        address _user
    )
        external
        isValidAddress(_user, "ResiToken: INVALID USER ADDRESS")
        validRole(_role)
        onlyRole(ADMIN_ROLE)
        whenNotPaused
    {
        _revokeRole(_role, _user);
        emit ResiRoleRemoved(_role, _user);
    }

    /**
     *  @dev internal function to check state of serie and project
     * @param _serieId serie id
     * @param _project project name
     */
    function _checkSerieAndProject(uint256 _serieId, bytes32 _project) internal view onlyRole(ADMIN_ROLE) {
        require(
            IResiRegistry(RESI_REGISTRY).isValidProject(_serieId, _project),
            "ResiToken: INVALID OR INACTIVE SERIE OR PROJECT"
        );
    }

    /**
     *  @dev It is not allowed to transfer resi token
     */
    function transfer(address, uint256) public pure override(ERC20Upgradeable) returns (bool) {
        revert TransferForbidden("RESIToken: NO TRANSFER ALLOWED");
    }

    /**
     * @dev It is not allowed to transfer resi token
     */
    function transferFrom(address, address, uint256) public pure override(ERC20Upgradeable) returns (bool) {
        revert TransferFromForbidden("RESIToken: NO TRANSFER FROM ALLOWED");
    }

    /**
     *  @dev Award user with ResiTokens
     * @param _account user to award
     * @param _role user role
     * @param _amount amount to award
     */
    function award(
        address _account,
        bytes32 _role,
        uint256 _amount
    ) external isValidAddress(_account, "ResiToken: INVALID RECEIVER ADDR") onlyRole(ADMIN_ROLE) nonReentrant {
        require(hasRole(_role, _account), "ResiToken: ACCOUNT HAS NOT VALID ROLE");
        address SERIE_SBT = IResiRegistry(RESI_REGISTRY).getSBTSerie();
        if (IERC721Upgradeable(SERIE_SBT).balanceOf(_account) == 0) {
            IResiSBT(SERIE_SBT).mintByResiToken(_account, _role);
        }
        _mint(_account, _amount);
        uint256 activeSerie = IResiRegistry(RESI_REGISTRY).activeSerie();
        IResiSBT(SERIE_SBT).increaseResiTokenBalance(_account, _amount);
        IResiRegistry(RESI_REGISTRY).increaseSerieSupply(activeSerie, _amount);
        emit ResiMinted(_account, _amount);
    }

    /**
     * @dev Perform exit to receive serie funds
     * @param _serieId serie id
     * @param _role user role
     */
    function exit(uint256 _serieId, bytes32 _role) external nonReentrant {
        _checkExit(_role);
        address SERIE_SBT = IResiRegistry(RESI_REGISTRY).getSBTSerie(_serieId);
        require(SERIE_SBT != address(0), "ResiToken: NO SBT SERIE SET");
        require(IERC20(SERIE_SBT).balanceOf(_msgSender()) == 1, "ResiToken: USER HAS NO SBT");
        uint256 resiSerieBalance = this.balanceOf(_msgSender());

        IResiRegistry(RESI_REGISTRY).withdrawFromVault(_serieId, resiSerieBalance, _msgSender());
        IResiSBT(SERIE_SBT).decreaseResiTokenBalance(_msgSender(), resiSerieBalance);
        _transfer(_msgSender(), IResiRegistry(RESI_REGISTRY).getTreasuryVault(), resiSerieBalance);

        emit Exit(_msgSender(), resiSerieBalance, _serieId);
    }

    /**
     *  @dev burn Resi Token
     * @param _amount amount to burn
     */
    function burn(uint256 _amount, uint256 _serieId) external {
        require(hasRole(TREASURY_ROLE, _msgSender()) || hasRole(ADMIN_ROLE, _msgSender()), "ResiToken: INVALID ROLE");
        ERC20BurnableUpgradeable.burn(_amount);
        IResiRegistry(RESI_REGISTRY).decreaseSerieSupply(_serieId, _amount);
        emit ResiBurnt(_msgSender(), _amount);
    }

    /**************************** INTERNALS  ****************************/

    /**
     * @dev Internal function to add roles batch function
     * @param role role to add
     * @param _addresses users array
     */
    function _addRolesBatch(
        bytes32 role,
        address[] memory _addresses
    ) internal onlyRole(ADMIN_ROLE) validRole(role) whenNotPaused {
        for (uint8 i = 0; i < _addresses.length; i++) {
            if (_addresses[i] == address(0)) {
                revert InvalidAddress(_addresses[i]);
            }
            _grantRole(role, _addresses[i]);
        }
    }

    /**
     * @dev Internal function to perform valid exit
     * @param _role user role
     */
    function _checkExit(bytes32 _role) internal view {
        require(_role != TREASURY_ROLE && _role != ADMIN_ROLE, "ResiToken: INVALID ACTION");
        require(hasRole(_role, _msgSender()), "ResiToken: ACCOUNT HAS NOT VALID ROLE");
        require(balanceOf(_msgSender()) > 0, "ResiToken: User HAS NO FUNDS TO EXIT");
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20Upgradeable, ERC20PausableUpgradeable) {
        super._beforeTokenTransfer(from, to, amount);
    }

    /**
     * @dev check address is not the zero address
     */
    modifier isValidAddress(address _addr, string memory message) {
        require(_addr != address(0), message);
        _;
    }

    /**
     * @dev check user has valid role
     */
    modifier validRole(bytes32 _role) {
        require(
            _role == MENTOR_ROLE || _role == PROJECT_BUILDER_ROLE || _role == RESI_BUILDER_ROLE,
            "ResiToken: INVALID ROLE"
        );
        _;
    }

    // Leave a gap betweeen inherited contracts variables in order
    // to be able to add more variables in them later.
    uint256[20] private upgradeGap;
}

contracts/ResiVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "./interfaces/IResiVault.sol";
import "./interfaces/IResiRegistry.sol";

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract ResiVault is IResiVault, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    ///@dev Main token address
    address public TOKEN;
    ///@dev RESI TOKEN address
    address public RESI_TOKEN;
    ///@dev RESI Registry adddress
    address public RESI_REGISTRY;
    ///@dev SERIE ID linked to Vault
    uint256 public SERIE_ID;

    mapping(bytes32 => address) public tokens;

    using SafeERC20 for IERC20;

    function initialize(
        uint256 _serieId,
        address _resiToken,
        address _token,
        address _resiRegistry
    ) public initializer {
        require(_resiToken != address(0), "ResiVault: INVALID RESI TOKEN ADDRESS");
        require(_resiRegistry != address(0), "ResiVault: INVALID RESI REGISTRY ADDRESS");
        __Context_init_unchained();
        __Ownable_init_unchained();
        __ReentrancyGuard_init_unchained();
        SERIE_ID = _serieId;
        TOKEN = _token;
        RESI_TOKEN = _resiToken;
        RESI_REGISTRY = _resiRegistry;
        emit ResiVaultInitialized(_serieId, _resiToken, _resiRegistry);
    }

    /**************************** GETTERS  ****************************/

    /**
     * @dev Get specific token Vault balance
     * @param _name token name
     * @return Contract token balance
     */
    function tokenBalance(bytes32 _name) external view returns (uint256) {
        require(tokens[_name] != address(0), "ResiVault: INVALID TOKEN NAME");
        return IERC20(tokens[_name]).balanceOf(address(this));
    }

    /**
     * @dev Get Vault native balance
     * @return native contract balance
     */
    function balance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Get main token
     * @return Main token address
     */
    function getMainToken() external view returns (address) {
        return TOKEN;
    }

    /**
     * @dev Get current exit quote given amount of tokens
     * @param _amount amount of tokens held by an user
     * @return exit quote
     */
    function getCurrentExitQuote(uint256 _amount) external view returns (uint256) {
        return _amount * _getExitQuote();
    }

    /**************************** INTERFACE  ****************************/

    /**
     *  @dev Set Main Token address
     * @param _token token address
     */
    function setMainToken(address _token) external onlyOwner {
        require(_token != address(0), "ResiVault: INVALID TOKEN ADDRESS");
        address oldToken = TOKEN;
        TOKEN = _token;
        emit MainTokenUpdated(oldToken, TOKEN);
    }

    /**
     *  @dev add new token
     * @param _token token address
     * @param _name token name
     */
    function addToken(address _token, bytes32 _name) external onlyOwner {
        require(_token != address(0), "ResiVault: INVALID TOKEN ADDRESS");
        require(_name != bytes32(0), "ResiVault: INVALID TOKEN NAME");
        require(tokens[_name] == address(0), "ResiVault: TOKEN ALREADY SET");
        tokens[_name] = _token;
        emit TokenAdded(_name, _token);
    }

    /**
     *  @dev remove token
     * @param _name token name
     */
    function removeToken(bytes32 _name) external onlyOwner {
        require(tokens[_name] != address(0), "ResiVault: INVALID TOKEN NAME");
        address _token = tokens[_name];
        tokens[_name] = address(0);
        emit TokenRemoved(_name, _token);
    }

    /**
     * @dev Transfer vault funds to ResiRegistry
     * @param _amount amount to transfer
     */
    function release(uint256 _amount) external onlyResiRegistry {
        require(_amount > 0, "INVALID AMOUNT");
        require(IERC20(TOKEN).balanceOf(address(this)) >= _amount, "ResiVault: INVALID AMOUNT TO RELEASE");

        uint256 quote = _getExitQuote();
        require(quote > 0, "ResiVault: Invalid quote");

        IERC20(TOKEN).safeTransfer(RESI_REGISTRY, quote * _amount);

        emit TokenReleased(TOKEN, _amount);
    }

    /**************************** INTERNALS  ****************************/

    /**
     * @dev Internal function to view current exit quote
     */
    function _getExitQuote() internal view returns (uint256) {
        uint256 serieSupply = IResiRegistry(RESI_REGISTRY).getSerieSupply(SERIE_ID);
        require(serieSupply > 0, "ResiVault: SERIE WITH NO MINTED SUPPLY");
        uint256 currentBalance = IERC20(TOKEN).balanceOf(address(this));
        uint256 quote = currentBalance / serieSupply;
        return quote;
    }

    /**
     * @dev recieve eth function
     */
    receive() external payable {
        emit EtherReceived(_msgSender(), msg.value);
    }

    /**
     * @dev check msg sender is ResiRegistry
     */
    modifier onlyResiRegistry() {
        require(_msgSender() == RESI_REGISTRY);
        _;
    }

    // Leave a gap betweeen inherited contracts variables in order
    // to be able to add more variables in them later.
    uint256[20] private upgradeGap;
}
